package com.limijiaoyin.socialsdk.utils;import android.content.Context;import android.content.SharedPreferences;import android.content.SharedPreferences.Editor;import android.telephony.TelephonyManager;public class Config {    private static SharedPreferences genericSharedPrefs;    private static final String GENERIC_CONFIG_PREFERENCE_NAME = "com.bestgames.share";    private static SharedPreferences getSharedPreferences(String name,            Context ctx) {        return ctx.getSharedPreferences(name, Context.MODE_WORLD_WRITEABLE);    }    private static synchronized SharedPreferences getGenericSharedPrefs(            Context ctx) {        if (genericSharedPrefs == null) {            genericSharedPrefs = getSharedPreferences(                    GENERIC_CONFIG_PREFERENCE_NAME, ctx);        }        return genericSharedPrefs;    }    public static boolean getActiveSina(Context ctx) {        return getGenericSharedPrefs(ctx.getApplicationContext()).getBoolean(                "sina", false);    }    public static void setActiveSina(boolean active, Context ctx) {        Editor editor = getGenericSharedPrefs(ctx.getApplicationContext())                .edit();        editor.putBoolean("sina", active);        editor.commit();    }    public static boolean getActiveQQ(Context ctx) {        return getGenericSharedPrefs(ctx.getApplicationContext()).getBoolean(                "qq", false);    }    public static void setActiveQQ(boolean active, Context ctx) {        Editor editor = getGenericSharedPrefs(ctx.getApplicationContext())                .edit();        editor.putBoolean("qq", active);        editor.commit();    }    public static boolean getActiveRenren(Context ctx) {        return getGenericSharedPrefs(ctx.getApplicationContext()).getBoolean(                "renren", false);    }    public static void setActiveRenren(boolean active, Context ctx) {        Editor editor = getGenericSharedPrefs(ctx.getApplicationContext())                .edit();        editor.putBoolean("renren", active);        editor.commit();    }        public static void cleanLocalStorage(Context ctx){        Config.setActiveQQ(false, ctx);        Config.setActiveRenren(false, ctx);        Config.setActiveSina(false, ctx);    }    public static String getDeviceInfo(Context ctx) {        TelephonyManager tm = (TelephonyManager) ctx                .getSystemService(Context.TELEPHONY_SERVICE);        String deviceId = tm.getDeviceId();        String imei = tm.getSimSerialNumber();        String imsi = tm.getSubscriberId();        String data = deviceId + "_" + imei + "_" + imsi + "_";        try {            data = MD5Utils.md5Digest(data);        } catch (Exception e) {        }        return data;    }}